package factorypack;

public class Customer {
	private String name;
	private int id;

	public Customer(int id) {
		// TODO Auto-generated constructor stub
		this.id = id;
	}
	public String getName() {
		return name;
	}
	
	public int getId() {
		return id;
	}

	public void setName(String name) {
		this.name = name;
	}
	
}
